n, err := c.Read(data)
if err != nil {
   // handle error
}
data = data[:n]

s := bufio.NewScanner(c)
if s.Scan() {
    data = s.Bytes() // data is next line, not including end lines, etc.
}
if s.Err() != nil {
    // handle error
}

package main
import "strings"

func main() {
   r, b := strings.NewReader("north east south west"), make([]byte, 16)
   n, e := r.Read(b)
   if e != nil {
      panic(e)
   }
   b = b[:n]
   println(string(b) == "north east south")
}

